mapper.select:
select

from T_LOGIN_LOG


        and ID = #{id,jdbcType=VARCHAR}


        and TRACE_ID = #{traceId,jdbcType=VARCHAR}


        and LOGIN_TYPE = #{loginType,jdbcType=VARCHAR}


        and USER_NAME = #{userName,jdbcType=VARCHAR}


        and USER_ID = #{userId,jdbcType=VARCHAR}


        and DEVICE = #{device,jdbcType=VARCHAR}


        and LOGIN_IP = #{loginIp,jdbcType=VARCHAR}


        and USER_AGENT = #{userAgent,jdbcType=VARCHAR}


        and LOGIN_RESULT = #{loginResult,jdbcType=VARCHAR}


        and FAIL_REASON = #{failReason,jdbcType=VARCHAR}


        and LOGOUT_DATETIME = #{logoutDatetime,jdbcType=TIMESTAMP}


        and LOGIN_DATETIME = #{loginDatetime,jdbcType=TIMESTAMP}


        and CREATE_DATETIME = #{createDatetime,jdbcType=TIMESTAMP}

mapper.update:
update T_LOGIN_LOG
ID = #{id,jdbcType=VARCHAR}, TRACE_ID = #{traceId,jdbcType=VARCHAR}, LOGIN_TYPE = #{loginType,jdbcType=VARCHAR}, USER_NAME = #{userName,jdbcType=VARCHAR}, USER_ID = #{userId,jdbcType=VARCHAR}, DEVICE = #{device,jdbcType=VARCHAR}, LOGIN_IP = #{loginIp,jdbcType=VARCHAR}, USER_AGENT = #{userAgent,jdbcType=VARCHAR}, LOGIN_RESULT = #{loginResult,jdbcType=VARCHAR}, FAIL_REASON = #{failReason,jdbcType=VARCHAR}, LOGOUT_DATETIME = current_timestamp, LOGIN_DATETIME = current_timestamp, CREATE_DATETIME = current_timestamp,
id = #{id, jdbcType = VARCHAR}

mapper.delete:
delete from T_LOGIN_LOG
where
id = #{id, jdbcType = VARCHAR}
