mapper.select:
select

from T_OPERATE_LOG


        and ID = #{id,jdbcType=VARCHAR}


        and LOGIN_ID = #{loginId,jdbcType=VARCHAR}


        and TRACE_ID = #{traceId,jdbcType=VARCHAR}


        and USER_ID = #{userId,jdbcType=VARCHAR}


        and APP = #{app,jdbcType=VARCHAR}


        and HOST_NAME = #{hostName,jdbcType=VARCHAR}


        and ACTION = #{action,jdbcType=VARCHAR}


        and URL = #{url,jdbcType=VARCHAR}


        and CONTENT = #{content,jdbcType=VARCHAR}


        and OPERATE_RESULT = #{operateResult,jdbcType=VARCHAR}


        and COMPLETE_DATETIME = #{completeDatetime,jdbcType=TIMESTAMP}


        and UPDATE_DATETIME = #{updateDatetime,jdbcType=TIMESTAMP}


        and CREATE_DATETIME = #{createDatetime,jdbcType=TIMESTAMP}

mapper.update:
update T_OPERATE_LOG
ID = #{id,jdbcType=VARCHAR}, LOGIN_ID = #{loginId,jdbcType=VARCHAR}, TRACE_ID = #{traceId,jdbcType=VARCHAR}, USER_ID = #{userId,jdbcType=VARCHAR}, APP = #{app,jdbcType=VARCHAR}, HOST_NAME = #{hostName,jdbcType=VARCHAR}, ACTION = #{action,jdbcType=VARCHAR}, URL = #{url,jdbcType=VARCHAR}, CONTENT = #{content,jdbcType=VARCHAR}, OPERATE_RESULT = #{operateResult,jdbcType=VARCHAR}, COMPLETE_DATETIME = current_timestamp, UPDATE_DATETIME = current_timestamp, CREATE_DATETIME = current_timestamp,
id = #{id, jdbcType = VARCHAR}

mapper.delete:
delete from T_OPERATE_LOG
where
id = #{id, jdbcType = VARCHAR}
